INSERT INTO movie (id, title, description, duration, image_url, trailer_url, price, release_date, production, director, genres) VALUES
  (1, 'KUNG FU PANDA 4', 'Po ostatnich niebezpiecznych przygodach, w których pokonał światowej klasy złoczyńców dzięki swojej niezrównanej odwadze i szalonym umiejętnościom sztuk walki, PO, Smoczy Wojownik zostaje wezwany przez przeznaczenie, aby... dać sobie już spokój. A dokładniej, ma zostać duchowym przywódcą Doliny Spokoju. Wiąże się z tym kilka oczywistych problemów. Po pierwsze, PO wie tyle samo o duchowym przywództwie, co o diecie paleo, a po drugie, musi szybko znaleźć i wyszkolić nowego Smoczego Wojownika, zanim będzie mógł objąć swoje nowe stanowisko. Co gorsza, do gry wkracza czarodziejka-Kameleona, maleńka jaszczurka, która może zmieniać kształt w dowolne stworzenie, duże lub małe. PO będzie potrzebował pomocy. Znajdzie ją u bystrej lisicy Zhen, której umiejętności okażą się nieocenione. W dążeniu do ochrony Doliny Spokoju przed Kameleoną, ten komediowy duet będzie musiał współpracować. PO odkryje, że przyjaciół można znaleźć w najbardziej nieoczekiwanych', 94, 'https://multikino.pl/-/media/images/film-and-events/2024/kung-fu-panda-4/kung-fu-panda-4-plakat-online-cut.jpg?h=477&w=319', 'https://www.youtube.com/embed/_inKs4eeHiI?si=1mY9jRmskSmSZSex', 19.99, '8 mar 2024', 'USA, 2023', 'Mike Mitchell', 'Animowany'),
  (2, 'GARFIELD', 'Garfield jest najbardziej znanym kotem na świecie. Jest leniuchem, który uwielbia lasagne i nie cierpi poniedziałków. Po nieoczekiwanym spotkaniu ze swoim dawno zaginionym ojcem – niechlujnym ulicznym kotem Vicem– Garfield i jego psi przyjaciel Odie zmuszeni są porzucić swoje doskonałe leniwe życie i dołączyć do Vica w zabawnym napadzie.', 101, 'https://multikino.pl/-/media/images/film-and-events/2024/garfield/garfiled-plakat.jpg', 'https://www.youtube.com/embed/BfOSzWu5Ecg?si=9HuWvyI-_8jq7XDR', 19.99, '31 maj 2024', 'USA, 2024', 'Mark Dindal', 'Animowany'),
  (3, 'W GŁOWIE SIĘ NIE MIEŚCI 2', 'Wyprodukowany przez Disneya i Pixara film „W głowie się nie mieści 2” to powrót do umysłu Riley, obecnie świeżo upieczonej nastolatki. Centrala sterowania przechodzi gruntowny remont, bo pojawiły się nowe Emocje! Radość, Smutek, Złość, Strach i Odraza, które dotąd z powodzeniem kierowały całym przedsięwzięciem, z pewnym wahaniem witają nowego przybysza: Niepewność.', 100, 'https://multikino.pl/-/media/images/film-and-events/2024/w-glowie-sie-nie-miesci-2/w-glowie-plakat-net-cut.jpg', 'https://www.youtube.com/embed/j5Dx0cdqbdM?si=GcoG7fOqdPfpOJ2X', 19.99, '14 cze 2024', 'USA, 2024', 'Kelsey Mann', 'Animowany'),
  (4, 'GRU I MINIONKI: POD PRZYKRYWKĄ', 'W tym roku 5 lipca, zobaczymy nowy rozdział, w którym Gru i Lucy oraz ich córeczki: - Margo , Edith i Agnes powitają nowego członka rodziny Gru juniora. Gru będzie musiał zmierzyć się z nowym nemezis w postaci Maxima Le Mala i jego femme fatale, Valentiny.', 111, 'https://multikino.pl/-/media/images/film-and-events/2024/gru-i-minionki-pod-przykrywka/minionki-i-gru-plakat-zastepczy.jpg', 'https://www.youtube.com/embed/8MoJCMc4uTI?si=vMQ5QgriHtpvecAb', 19.99, '5 lip 2024', 'USA, 2024', 'Steve Carrell', 'Animowany'),
  (5, 'DIUNA: CZĘŚĆ DRUGA', 'Część druga ukazuje imponującą podróż Paula Atrydy, który zawiera sojusz z Chani i Fremenami. Jednocześnie wkracza na wojenną ścieżkę, pałając żądzą zemsty na spiskowcach, którzy doprowadzili do upadku jego rodziny. Stając przed wyborem między miłością życia a losem znanego wszechświata, Paul pragnie zapobiec tragicznej przyszłości, którą tylko on potrafi przewidzieć.', 166, 'https://multikino.pl/-/media/images/film-and-events/2024/diuna-czesc-druga/diuna2_plakat-final-cut.jpg', 'https://www.youtube.com/embed/ouSfZnsGIUQ?si=sKqwRk4BLt3Vx-cD', 19.99, '1 mar 2024', 'USA, Kanada, 2024', 'Denis Villeneuve', 'Akcja'),
  (6, 'GODZILLA I KONG: NOWE IMPERIUM', 'Walka gigantów trwa!. Potężny Kong i przeraźliwy Godzilla są zmuszeni stawić czoło kolosalnemu, nieodkrytemu zagrożeniu, kryjącemu się w naszym świecie. Stawia ono pod znakiem zapytania istnienie potworów… oraz nasze. Godzilla i Kong: Nowe imperium dokładniej zgłębia historię tych tytanów oraz ich początki, jak również tajemnice Wyspy Czaszki i nie tylko. Jednocześnie ukazuje mityczną batalię, która przyczyniła się do powstania tych niezwykłych istot i związania ich losu z ludzkością już na zawsze.', 113, 'https://multikino.pl/-/media/images/film-and-events/2024/godzilla-i-kong-nowe-imperium/godzilla-kong-plakat-final-cut.jpg', 'https://www.youtube.com/embed/lV1OOlGwExM?si=-59Y5nekIugrgP0Q', 19.99, '28 mar 2024', 'USA, 2023', 'ADAM WINGARD', 'Akcja'),
  (7, 'ISTOTY FANTASTYCZNE', 'To historia dziewczynki, która odkrywa, że może zobaczyć wyimaginowanych przyjaciół wszystkich, których zna. Co robi z tą supermocą? Wyruszy w magiczną przygodę, aby połączyć zapomniane Istoty fantastyczne z tymi dla których są ważne. W filmie występują Cailey Fleming, Ryan Reynolds, John Krasinski, Fiona Shaw oraz głosów użyczają: Phoebe Waller Bridge, Louis Gossett Jr. i Steve Carell, a także wiele innych postaci, które odzwierciedlają niesamowitą moc dziecięcej wyobraźni.', 108, 'https://multikino.pl/-/media/images/film-and-events/2024/istoty-fantastyczne/istoty-fantastyczne-plakat-online-cut.jpg', 'https://www.youtube.com/embed/EN-a6AH2ZNE?si=4RHpyl187qSBjPH9', 19.99, '17 maj 2024', 'USA, 2024', 'John Krasinski', 'Dramat'),
  (8, 'KASKADER', 'Bohater filmu jest kaskaderem i jak każdy w tym zawodzie, zdarza się, że zostanie wysadzony w powietrze, że zostanie postrzelony i wyrzucony przez okno oraz zrzucony z wysokości, a wszystko to dla naszej rozrywki. Teraz, po prawie kończącym karierę wypadku, nasz bohater musi odnaleźć zaginioną gwiazdę filmową, rozwiązać spisek i spróbować odzyskać miłość swojego życia, wciąż wykonując swoją codzienną pracę. Co może się wydarzyć?', 127, 'https://multikino.pl/-/media/images/film-and-events/2024/kaskader/kaskader-plakat-online-cut.jpg', 'https://www.youtube.com/embed/4gJZFiu0PKg?si=xYeYuc6YarF9k3Ok', 19.99, '3 maj 2024', 'USA, 2023', 'Davida Leitch', 'Akcja'),
  (9, 'KRÓLESTWO PLANETY MAŁP', 'W świecie zdominowanym przez żyjące w harmonii małpy ludzie zostali zepchnięci na margines. Gdy nowy przywódca małp buduje swoje imperium, młody samiec wyrusza w niezwykle trudną podróż, która zmieni jego dotychczasowe wyobrażenie o przeszłości, a nowe decyzje wpłyną na przyszłość zarówno małp jak i ludzi.', 145, 'https://multikino.pl/-/media/images/film-and-events/2024/krolestwo-planety-malp/planeta-plakat-net-cut.jpg', 'https://www.youtube.com/embed/CcuPXCWmGAQ?si=jtbfBTFjE22Hvn-9', 19.99, '7 mar 2023', 'USA, 2024', 'Mike Mitchell', 'Akcja'),
  (10, 'MÓJ PIES ARTUR', 'Miłośnik sportu i adrenaliny Michael Light (Mark Wahlberg) staje przed największym wyzwaniem w dotychczasowej karierze. Wraz z trzyosobową ekipą zaufanych zawodników przystępuje do Mistrzostw Świata w Rajdach Przygodowych na Dominikanie. Stając na starcie ekstremalnych zmagań, w czasie których – przez 10 dni – pieszo, kajakami i na rowerze będzie musiał pokonać prawie 700 kilometrów, Light nie przypuszcza, że wkrótce jego zespół rozrośnie się o dodatkowego członka. Będzie nim jedyny w swoim rodzaju, bohaterski pies – Artur. Połączeni nierozerwalną więzią człowiek i wierny czworonóg rzucą wyzwanie dżungli, falom, przepaściom i ograniczeniom własnych organizmów. To co wspólnie osiągną, napisze niebywałą historię, która poruszy i zachwyci cały świat.', 97, 'https://multikino.pl/-/media/images/film-and-events/2024/moj-pies-artur/moj-pies-artur_plakat_cut.jpg', 'https://www.youtube.com/embed/_koQEI34KSQ?si=3CM3TbkIdPseYBoJ', 19.99, '19 sie 2024', 'USA, 2022', 'Mike Mitchell', 'Dramat'),
  (11, 'FURIOSA: SAGA MAD MAX', 'Kiedy świat upada, młoda Furiosa zostaje uprowadzona z Zielonego Miejsca Wielu Matek. Wpada w ręce potężnej Hordy Bikerów, której przewodzi watażka Dementus. Po przebyciu Pustkowi porywacze docierają do Cytadeli, gdzie rządzi Wieczny Joe. Dwóch tyranów zaczyna walkę o władzę, Furiosa zaś musi przetrwać wiele prób, jednocześnie gromadząc środki, które pozwolą jej wrócić do domu.', 148, 'https://multikino.pl/-/media/images/film-and-events/2024/furiosa-saga-mad-max/pl_poland_friosa_cut.jpg', 'https://www.youtube.com/embed/XotnyLjS8fo?si=49hHLWn969MvfHM3', 19.99, '24 maj 2024', 'USA, 2024', 'GEORGE MILLER', 'Akcja'),
  (12, 'HIT MAN', 'Na co dzień skryty i nieśmiały profesor filozofii Gary Johnson (Glen Powell), pokazuje drugie oblicze, dorabiając jako „fałszywy zabójca” na usługach Departamentu Policji z Nowego Orleanu. Obdarzony niespotykanym talentem do zdumiewających metamorfoz i przebieranek, mężczyzna nie ma sobie równych w wyłapywaniu ludzi planujących morderstwa swoich wrogów. Co jednak, gdy – podszywając się pod seksownego killera – sam znajdzie się pod wpływem i urokiem kobiety, którą zamierzał posłać za kratki? Czy uwikłany w gorący romans zdoła oprzeć się pięknej Madison (Adria Arjona), czy też sam stanie się ofiarą w tej przebiegłej grze pozorów?', 115, 'https://multikino.pl/-/media/images/film-and-events/2024/hit-man/plakat_hit_man_cut.jpg', 'https://www.youtube.com/embed/0g4cJ4NE8HA?si=29MfUISk7UgmrMBv', 19.99, '17 maj 2024', 'USA, 2023', 'Richard Linklater', 'Komedia'),
  (13, 'PUCHATEK: KREW I MIÓD 2', 'Żądny mordu Puchatek powraca w towarzystwie nowych przyjaciół, aby zgładzić nie tylko Krzysia, ale wszystkich pozostałych przy życiu uczestników pewnego urodzinowego przyjęcia sprzed lat.', 100, 'https://multikino.pl/-/media/images/film-and-events/2024/puchatek-krew-i-miod-2/puchatek_2_puchatek_v2_popr_cut.jpg', 'https://www.youtube.com/embed/FHDp34K8gbg?si=cCt1TchjbJ473a4r', 19.99, '31 maj 2024', 'Wielka Brytania, 2024', 'Rhys Frake-Waterfield', 'Horror'),
  (14, 'STRAŻNICZKA SMOKÓW', 'W starożytnych Chinach smoki żyły w przyjaźni z ludźmi. Niestety, ludzka chciwość zakończyła ten sojusz. Wiele lat później mała sierota Ping dba o ostatnią parę uwięzionych smoków. Dziewczynka pomaga swej opiekunce w pałacowym gospodarstwie. Próbuje też trzymać z dala od kłopotów niesforną przyjaciółkę, myszkę Hua Hua, która ciągle przeszkadza w obowiązkach. Pewnego dnia Ping dowiaduje się o swoim prawdziwym przeznaczeniu – musi uratować niezwykle cenne, smocze jajo. Dziewczynka, w towarzystwie starego smoka, wyrusza w pełną wyzwań podróż, by odnaleźć magiczną wodę. Tylko tam przychodzą bowiem na świat nowe smoki. Podczas tej niebezpiecznej przygody Ping znajdzie sposób na uwolnienie swej mocy i odkryje, że jest prawdziwą Strażniczką Smoków.', 99, 'https://multikino.pl/-/media/images/film-and-events/2024/strazniczka-smokow/strazniczka-smokow_plakat_cut.jpg', 'https://www.youtube.com/embed/xwJHIVtXxLQ?si=X6momd6jIBvifgP2', 19.99, '24 maj 2024', 'USA, 2024', 'Salvador Simó', 'Animowany'),
  (15, 'CHŁOPI', '„CHŁOPI” zostaną zrealizowani w technice animacji malarskiej, która podbiła serca fanów na całym świecie przy poprzedniej produkcji studia – filmie „Twój Vincent”. Historia filmu oparta jest na powieści Władysława Reymonta, który w 1924 roku otrzymał Literacką Nagrodę Nobla wyprzedzając Thomasa Manna, Georga Bernarda Shawa i Thomasa Hardyego.', 116, 'https://multikino.pl/-/media/images/film-and-events/2023/chlopi/chlopi_plakat-oficjalny_cut.jpg', 'https://www.youtube.com/embed/rILKSimhfA4?si=lluja7t2gzgv97Dy', 19.99, '13 paź 2023', 'Polska, 2021', 'Dorota Kobiela', 'Dramat');
